import styled from "styled-components"

const Container = styled.div`

display: grid;
  height: 40px;
  align-content: end;
  grid-template-columns: auto auto auto;
  gap: 10px;
  background-color: #2196F3;
  padding: 10px;

  text-align: center;
  padding: 20px 0;
  font-size: 30px;
`

export { Container }



